import React, { useState, useEffect } from "react";

const API_URL = "http://localhost:5000"; // Change if backend runs elsewhere

function App() {
    const [projects, setProjects] = useState([]);
    const [newProject, setNewProject] = useState("");
    const [error, setError] = useState("");
    const [total, setTotal] = useState(0);

    // 1️⃣ Fetch all projects
    const fetchProjects = async () => {
        const res = await fetch(`${API_URL}/api/projects`);
        const data = await res.json();
        setProjects(data.projects);
    };

    // 2️⃣ Fetch project count
    const fetchProjectCount = async () => {
        const res = await fetch(`${API_URL}/api/projects/count`);
        const data = await res.json();
        setTotal(data.total);
    };

    // 3️⃣ Add a project
    const addProject = async () => {
        setError(""); // Clear errors
        if (newProject.length < 3) {
            setError("Project name must be at least 3 characters");
            return;
        }

        const res = await fetch(`${API_URL}/api/projects`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ name: newProject }),
        });

        const data = await res.json();
        if (!res.ok) {
            setError(data.message); // Show error from backend
            return;
        }

        setProjects([...projects, data.project]); // Update UI
        setNewProject("");
        fetchProjectCount(); // Refresh count
    };

    // 4️⃣ Delete a project
    const deleteProject = async (id) => {
        await fetch(`${API_URL}/api/projects/${id}`, { method: "DELETE" });
        setProjects(projects.filter(p => p.id !== id)); // Remove from UI
        fetchProjectCount(); // Refresh count
    };

    // Fetch projects & count on page load
    useEffect(() => {
        fetchProjects();
        fetchProjectCount();
    }, []);

    return (
        <div style={{ padding: "20px", fontFamily: "Arial" }}>
            <h1>Project Manager</h1>
            <h2>Total Projects: {total}</h2>

            <div>
                <input
                    type="text"
                    value={newProject}
                    onChange={(e) => setNewProject(e.target.value)}
                    placeholder="Enter project name"
                />
                <button onClick={addProject}>Add Project</button>
            </div>

            {error && <p style={{ color: "red" }}>{error}</p>}

            <ul>
                {projects.map((project) => (
                    <li key={project.id}>
                        {project.name}
                        <button onClick={() => deleteProject(project.id)} style={{ marginLeft: "10px" }}>
                            Delete
                        </button>
                    </li>
                ))}
            </ul>

            <button onClick={fetchProjects}>Refresh Projects</button>
        </div>
    );
}

export default App;
